mapper.update:
update weather_province
set code = #{code,jdbcType=VARCHAR},
  country_id = #{countryId,jdbcType=BIGINT},
  name = #{name,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

